package first;

public class aritm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15;
		int b=4;
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a*b="+(a*b));
		System.out.println("a/b="+(a/b));
		System.out.println("a%b="+(a%b));
		System.out.println("a++="+(a++));
		System.out.println("++b="+(++b));
		
		double x=15.0;
		double y=4.0;
		System.out.println("x/y="+(x/y));

	}

}
